// src\components\Home.js

import React from 'react';
import styled from 'styled-components';
import educationImage from '../assets/Education List.jpg';
import cert1 from '../assets/Certificated 1.jpg';
import cert2 from '../assets/Certificated 2.jpg';
import cert3 from '../assets/Certificated 3.jpg';
import cert4 from '../assets/Certificated 4.jpg';
import cert5 from '../assets/Certificated 5.jpg';
import cert6 from '../assets/Certificated 6.jpg';

const HomeWrapper = styled.div`
  text-align: center;
  padding: 4rem 2rem;
  background-color: #f4f4f9;
`;

const Title = styled.h1`
  font-size: 3rem;
  color: #333;
  margin-bottom: 1rem;
`;

const Subtitle = styled.h2`
  font-size: 2rem;
  color: #666;
  margin-bottom: 2rem;
`;

const Bio = styled.p`
  font-size: 1.2rem;
  color: #555;
  line-height: 1.6;
  max-width: 800px;
  margin: 0 auto 1.5rem auto;
`;

const Section = styled.div`
  margin-top: 2rem;
  text-align: left;
  max-width: 800px;
  margin-left: auto;
  margin-right: auto;
`;

const SectionTitle = styled.h3`
  font-size: 1.5rem;
  color: #333;
  margin-bottom: 1rem;
`;

const Image = styled.img`
  width: 100%;
  height: auto;
  margin-top: 2rem;
`;

const CertImagesWrapper = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 0; // 간격을 0으로 설정하여 이미지를 붙여서 표시합니다.
`;

const CertImage = styled.img`
  width: 33.33%; // 한 줄에 3개의 이미지를 표시합니다.
  height: auto;
`;

const Home = () => {
  return (
    <HomeWrapper>
      <Title>Hoi Won Kim</Title>
      <Subtitle>Software Developer</Subtitle>
      <Bio>
        I am a dedicated and enthusiastic software developer with a passion for creating innovative solutions. With a background in web development and experience in various programming languages and frameworks, I am committed to delivering high-quality software that meets user needs. My journey in the tech industry has been driven by a relentless pursuit of knowledge and a desire to stay at the forefront of technological advancements.
      </Bio>
      <Bio>
        Over the years, I have honed my skills in front-end and back-end development, working on projects that range from small business websites to large-scale applications. I thrive in collaborative environments and am always eager to take on new challenges that push the boundaries of my expertise.
      </Bio>
      <Section>
        <SectionTitle>Certifications</SectionTitle>
        <CertImagesWrapper>
          <CertImage src={cert1} alt="Certificate 1" />
          <CertImage src={cert2} alt="Certificate 2" />
          <CertImage src={cert3} alt="Certificate 3" />
          <CertImage src={cert4} alt="Certificate 4" />
          <CertImage src={cert5} alt="Certificate 5" />
          <CertImage src={cert6} alt="Certificate 6" />
        </CertImagesWrapper>
      </Section>
      <Section>
        <SectionTitle>Education</SectionTitle>
        <Image src={educationImage} alt="Education List" />
      </Section>
    </HomeWrapper>
  );
}

export default Home;
