// src\components\Projects.js

import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import ProjectCard from './ProjectCard';

const ProjectsWrapper = styled.div`
  padding: 4rem 2rem;
  background-color: #f4f4f9;
`;

const ProjectSectionTitle = styled.h2`
  text-align: center;
  font-size: 2rem;
  color: #333;
  margin-bottom: 2rem;
`;

const ProjectsContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: space-around;
`;

// 각 프로젝트에 맞는 이미지를 설정합니다.
const projectImages = {
  "my-portfolio": "https://images.unsplash.com/photo-1515378791036-0648a3ef77b2",
  "react-todo-app": "https://images.unsplash.com/photo-1523473827532-2d0e5a7d835b",
  "weather-app": "https://images.unsplash.com/photo-1537432376769-00a90e82d56a",
  "ecommerce-site": "https://images.unsplash.com/photo-1506765515384-028b60a970df",
  "blog-platform": "https://images.unsplash.com/photo-1498050108023-c5249f4df085",
  "chat-app": "https://images.unsplash.com/photo-1559027615-5fef28f9a215",
  "social-media-app": "https://images.unsplash.com/photo-1488590528505-98d2b5aba04b",
  "fitness-tracker": "https://images.unsplash.com/photo-1516483638261-f4dbaf036963",
  "recipe-app": "https://images.unsplash.com/photo-1517248135467-4c7edcad34c4",
  "travel-blog": "https://images.unsplash.com/photo-1506748686214-e9df14d4d9d0"
  // 추가 프로젝트 이미지 URL을 여기에 추가합니다.
};

const Projects = () => {
  const [repos, setRepos] = useState([]);

  useEffect(() => {
    fetch('https://api.github.com/users/hoiwonkim/repos')
      .then(response => response.json())
      .then(data => setRepos(data));
  }, []);

  return (
    <ProjectsWrapper>
      <ProjectSectionTitle>My Projects</ProjectSectionTitle>
      <ProjectsContainer>
        {repos.map(repo => (
          <ProjectCard
            key={repo.id}
            title={repo.name}
            description={repo.description}
            url={repo.html_url}
            image={projectImages[repo.name] || "https://via.placeholder.com/300x200.png?text=No+Image+Available"}
          />
        ))}
      </ProjectsContainer>
    </ProjectsWrapper>
  );
};

export default Projects;
