// src/components/Projects.js

import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import ProjectCard from './ProjectCard';

const ProjectsWrapper = styled.div`
  padding: 4rem 2rem;
  background-color: #f4f4f9;
`;

const ProjectSectionTitle = styled.h2`
  text-align: center;
  font-size: 2rem;
  color: #333;
  margin-bottom: 2rem;
`;

const ProjectsContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: space-around;
`;

const Projects = () => {
  const [repos, setRepos] = useState([]);

  const projectIcons = {
    "github-example": "ðŸ’»",
    "Rolling-Dices_KIM": "ðŸŽ²",
    "Module-2-Lab_HTML": "ðŸ“„",
    "Module_2.2_CSS": "ðŸŽ¨",
    "Module_2.1_CSS_Movie_Music_Attached": "ðŸŽ¬ðŸŽµ",
    "Module_2.3_CSS": "ðŸŽ¨",
    "20200530_class_exercise": "ðŸ“˜",
    "JS_Basic": "ðŸ“œ",
    "E-commerce": "ðŸ›’",
    "Next.js_Practice": "âš›ï¸",
    "React_Native_Project": "ðŸ“±",
    "WeatherApp": "ðŸŒ¦ï¸",
    // ì¶”ê°€ í”„ë¡œì íŠ¸ ì•„ì´ì½˜ì„ ì—¬ê¸°ì— ì¶”ê°€í•©ë‹ˆë‹¤.
  };

  useEffect(() => {
    fetch('https://api.github.com/users/hoiwonkim/repos')
      .then(response => response.json())
      .then(data => setRepos(data));
  }, []);

  return (
    <ProjectsWrapper>
      <ProjectSectionTitle>My Projects</ProjectSectionTitle>
      <ProjectsContainer>
        {repos.map(repo => (
          <ProjectCard
            key={repo.id}
            title={repo.name}
            description={repo.description}
            url={repo.html_url}
            icon={projectIcons[repo.name] || "ðŸ“"}
          />
        ))}
      </ProjectsContainer>
    </ProjectsWrapper>
  );
};

export default Projects;
