// src/components/Projects.js

import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import ProjectCard from './ProjectCard';

const ProjectsWrapper = styled.div`
  padding: 4rem 2rem;
  background-color: #f4f4f9;
`;

const ProjectSectionTitle = styled.h2`
  text-align: center;
  font-size: 2rem;
  color: #333;
  margin-bottom: 2rem;
`;

const ProjectsContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: space-around;
`;

const Projects = () => {
  const [repos, setRepos] = useState([]);

  const projectIcons = {
    "github-example": "💻",
    "Rolling-Dices_KIM": "🎲",
    "Module-2-Lab_HTML": "📄",
    "Module_2.2_CSS": "🎨",
    "Module_2.1_CSS_Movie_Music_Attached": "🎬🎵",
    "Module_2.3_CSS": "🎨",
    "20200530_class_exercise": "📘",
    "JS_Basic": "📜",
    "E-commerce": "🛒",
    "Next.js_Practice": "⚛️",
    "React_Native_Project": "📱",
    "WeatherApp": "🌦️",
    // 추가 프로젝트 아이콘을 여기에 추가합니다.
  };

  useEffect(() => {
    fetch('https://api.github.com/users/hoiwonkim/repos')
      .then(response => response.json())
      .then(data => setRepos(data));
  }, []);

  return (
    <ProjectsWrapper>
      <ProjectSectionTitle>My Projects</ProjectSectionTitle>
      <ProjectsContainer>
        {repos.map(repo => (
          <ProjectCard
            key={repo.id}
            title={repo.name}
            description={repo.description}
            url={repo.html_url}
            icon={projectIcons[repo.name] || "📁"}
          />
        ))}
      </ProjectsContainer>
    </ProjectsWrapper>
  );
};

export default Projects;
