// src\components\Projects.js

import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import ProjectCard from './ProjectCard';

const ProjectsWrapper = styled.div`
  padding: 4rem 2rem;
  background-color: #f4f4f9;
`;

const ProjectSectionTitle = styled.h2`
  text-align: center;
  font-size: 2rem;
  color: #333;
  margin-bottom: 2rem;
`;

const ProjectsContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: space-around;
`;

const Projects = () => {
  const [repos, setRepos] = useState([]);

  const projectImages = {
    "project1": "https://via.placeholder.com/300x200.png?text=Project+1",
    "project2": "https://via.placeholder.com/300x200.png?text=Project+2",
    "project3": "https://via.placeholder.com/300x200.png?text=Project+3",
    "project4": "https://via.placeholder.com/300x200.png?text=Project+4",
    "project5": "https://via.placeholder.com/300x200.png?text=Project+5",
    // 추가 프로젝트 이미지 URL을 여기에 추가하세요.
  };

  useEffect(() => {
    fetch('https://api.github.com/users/hoiwonkim/repos')
      .then(response => response.json())
      .then(data => setRepos(data));
  }, []);

  return (
    <ProjectsWrapper>
      <ProjectSectionTitle>My Projects</ProjectSectionTitle>
      <ProjectsContainer>
        {repos.map(repo => (
          <ProjectCard
            key={repo.id}
            title={repo.name}
            description={repo.description}
            url={repo.html_url}
            image={projectImages[repo.name.toLowerCase()] || "https://via.placeholder.com/300x200.png?text=Default+Image"}
          />
        ))}
      </ProjectsContainer>
    </ProjectsWrapper>
  );
};

export default Projects;
