// src\components\Home.js

import React from 'react';
import styled from 'styled-components';
import educationImage from '../assets/Education List.jpg'; // 이미지를 프로젝트에 추가하고 올바른 경로로 수정합니다.

const HomeWrapper = styled.div`
  text-align: center;
  padding: 4rem 2rem;
  background-color: #f4f4f9;
`;

const Title = styled.h1`
  font-size: 3rem;
  color: #333;
  margin-bottom: 1rem;
`;

const Subtitle = styled.h2`
  font-size: 2rem;
  color: #666;
  margin-bottom: 2rem;
`;

const Bio = styled.p`
  font-size: 1.2rem;
  color: #555;
  line-height: 1.6;
  max-width: 800px;
  margin: 0 auto 1.5rem auto;
`;

const Section = styled.div`
  margin-top: 2rem;
  text-align: left;
  max-width: 800px;
  margin-left: auto;
  margin-right: auto;
`;

const SectionTitle = styled.h3`
  font-size: 1.5rem;
  color: #333;
  margin-bottom: 1rem;
`;

const Item = styled.div`
  font-size: 1.2rem;
  color: #555;
  margin-bottom: 0.5rem;
`;

const Image = styled.img`
  width: 100%;
  height: auto;
  margin-top: 2rem;
`;

const Home = () => {
  return (
    <HomeWrapper>
      <Title>Hoi Won Kim</Title>
      <Subtitle>Software Developer</Subtitle>
      <Bio>
        I am a dedicated and enthusiastic software developer with a passion for creating innovative solutions. With a background in web development and experience in various programming languages and frameworks, I am committed to delivering high-quality software that meets user needs. My journey in the tech industry has been driven by a relentless pursuit of knowledge and a desire to stay at the forefront of technological advancements.
      </Bio>
      <Bio>
        Over the years, I have honed my skills in front-end and back-end development, working on projects that range from small business websites to large-scale applications. I thrive in collaborative environments and am always eager to take on new challenges that push the boundaries of my expertise.
      </Bio>
      <Section>
        <SectionTitle>Certifications</SectionTitle>
        <Item>Certification 1: Description of the certification and its relevance.</Item>
        <Item>Certification 2: Description of the certification and its relevance.</Item>
        <Item>Certification 3: Description of the certification and its relevance.</Item>
      </Section>
      <Section>
        <SectionTitle>Education</SectionTitle>
        <Image src={educationImage} alt="Education List" />
      </Section>
    </HomeWrapper>
  );
}

export default Home;
