// src\components\About.js

import React from 'react';
import styled from 'styled-components';

const AboutWrapper = styled.div`
  padding: 4rem 2rem;
  text-align: center;
  background-color: #f4f4f9;
`;

const Title = styled.h1`
  font-size: 2.5rem;
  color: #333;
  margin-bottom: 1rem;
`;

const Bio = styled.p`
  font-size: 1.2rem;
  color: #666;
  line-height: 1.6;
  max-width: 800px;
  margin: 0 auto 1.5rem auto;
`;

const ResumeLink = styled.a`
  font-size: 1rem;
  color: #007bff;
  text-decoration: none;
  &:hover {
    text-decoration: underline;
  }
`;

const About = () => {
  return (
    <AboutWrapper>
      <Title>About Me</Title>
      <Bio>
        I am a passionate software developer with a background in web development and a keen interest in learning new technologies. Over the years, I have developed a wide range of skills in various programming languages and frameworks. I am dedicated to building efficient and scalable applications that provide a seamless user experience.
      </Bio>
      <Bio>
        My journey began with a strong foundation in computer science, and I have continuously worked on honing my skills through various projects and collaborations. I enjoy solving complex problems and thrive in a collaborative environment where I can contribute to the success of the team.
      </Bio>
      <ResumeLink href="link-to-your-resume" target="_blank" rel="noopener noreferrer">
        View My Resume
      </ResumeLink>
    </AboutWrapper>
  );
}

export default About;
